INSERT INTO `cities` (`id`, `name`, `slug`, `image`, `description`, `meta_title`, `meta_description`, `meta_keywords`, `status`, `visible_on_homepage`, `created_at`, `updated_at`) VALUES
(1, 'लखनऊ', 'lkhanauu', 'cities/y88ZR5sHoN6aXwtMLYefqNagpOqSI4Ul2wjApGfl.jpg', 'लखनऊ, जिसे \'नवाबों का शहर\' कहते हैं, अपनी \'लखनवी तहजीब\', चिकनकारी कला और शानदार व्यंजनों (जैसे कबाब, बिरयानी, बास्केट चाट) के लिए मशहूर है, यहाँ बड़ा इमामबाड़ा की भूलभुलैया और रूमी दरवाजा जैसी ऐतिहासिक धरोहरें हैं, गोमती नदी के किनारे बसा है और भारत के स्वतंत्रता संग्राम में इसका अहम योगदान रहा है।', NULL, NULL, NULL, 1, 1, '2025-12-09 07:59:01', '2025-12-09 09:53:58'),
(2, 'आगरा', 'aagara', 'cities/HIjvtpbwCh1VnGQ71GVebMVbd2WTOE9IGQDKF9vJ.jpg', 'आगरा उत्तर प्रदेश का एक ऐतिहासिक और सांस्कृतिक रूप से समृद्ध शहर है, जो ताजमहल के कारण विश्वभर में प्रसिद्ध है। मुगल काल की अनगिनत धरोहरें यहाँ की शान बढ़ाती हैं, जिनमें आगरा किला और फ़तेहपुर सीकरी प्रमुख हैं। यमुना नदी के किनारे बसा यह शहर अपनी अद्भुत स्थापत्य कला और सौंदर्य के लिए पर्यटकों को आकर्षित करता है। आगरा की पेठा मिठाई और शहर की खास मंजिलें हर आगंतुक के अनुभव को यादगार बना देती हैं। समृद्ध इतिहास, अनोखी वास्तुकला और जीवंत बाज़ारों ने इसे पर्यटन का प्रमुख केंद्र बना दिया है।\r\nऐसी ही रोचक खबरें पढ़ने के लिए क्लिक करें।', NULL, NULL, NULL, 1, 1, '2025-12-09 08:00:31', '2025-12-09 09:59:49'),
(3, 'कानपुर', 'kanapara', 'cities/cFRRHwmSqxAWFWjRe2zWrJ3hFpvVjIfYGHkM7KKF.jpg', 'कानपुर की 7 सबसे अच्छी घूमने की जगहों में मोती झील, नाना राव पार्क, जंगलेश्वर मंदिर, ब्लू वर्ल्ड थीम पार्क, इस्कॉन मंदिर, फूलबाग और कानपुर ज़ू प्रमुख हैं। ये स्थान प्राकृतिक सुंदरता, इतिहास और मनोरंजन का बेहतरीन मिश्रण पेश करते हैं, जहाँ हर उम्र के लोग कुछ न कुछ खास अनुभव कर सकते हैं।\r\nऐसी ही रोचक खबरें पढ़ने के लिए क्लिक करें।', NULL, NULL, NULL, 1, 1, '2025-12-09 08:39:00', '2025-12-09 10:07:14');

