INSERT INTO `news` (`id`, `city_id`, `title`, `description`, `image`, `source_link`, `source_name`, `published_date`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'संघ के 100 साल: विभाजन से 1984 दंगों तक संघ का जोर सिखों से मजबूत रिश्तों पर रहा', 'देश के विभाजन के पूर्व अमृतसर में हिंसा हो रही थी. दंगाई सिखों को निशाना बना रहे थे. ऐसे नाजुक मौके पर स्वयंसेवकों ने पंजाब में गुरुद्वारों और सिखों की रक्षा की. इसके लिए दंगाइयों से कई जगहों पर उनका टकराव हुआ.  RSS के 100 सालों के सफर की 100 कहानियों की कड़ी …', 'https://akm-img-a-in.tosshub.com/aajtak/images/story/202601/69732c0e7707d-rss-news-230632923-16x9.jpg', 'https://www.aajtak.in/india/news/story/sangh-100-years-rss-sikh-antio-sikh-riot-1984-partition-india-ntcppl-dskc-2446779-2026-01-23', 'Aajtak.in', '2026-01-23 08:13:46', 1, '2026-01-24 09:36:48', '2026-01-24 09:36:48'),
(2, 1, 'Kashmir Snowfall: कश्मीर घाटी में भारी बर्फबारी, श्रीनगर एयरपोर्ट पर उड़ानें रद्द, एयरलाइंस ने जारी की एडवाइजरी', 'कश्मीर घाटी में शुक्रवार को हुई भारी बर्फबारी ने हवाई यात्रा को बाधित कर दिया, जिसके चलते श्रीनगर एयरपोर्ट पर कई फ्लाइट स्थगित और रद्द करनी पड़ीं और एयरलाइंस को यात्रियों के लिए ट्रैवल एडवाइजरी जारी करनी पड़ी। श्रीनगर एयरपोर्ट के अधिकारियों ने कहा कि खराब मौस…', 'https://images.moneycontrol.com/static-hindinews/2026/01/Kashmir-Snowfall.jpg', 'https://hindi.moneycontrol.com/india/heavy-snowfall-in-kashmir-valley-flights-cancelled-at-srinagar-airport-airlines-issue-advisory-article-2348450.html', 'Moneycontrol', '2026-01-23 04:31:49', 1, '2026-01-24 09:36:48', '2026-01-24 09:36:48'),
(3, 1, '23 और 26 जनवरी को इन 6 मेट्रो स्टेशनों के गेट रहेंगे बंद, DMRC ने किया ऐलान', 'Delhi Metro January 2026: गणतंत्र दिवस की तैयारियों को देखते हुए राजधानी में सुरक्षा के कड़े इंतजाम किए गए हैं। जिसको देखते हुए दिल्ली मेट्रो रेल कॉरपोरेशन (DMRC) ने 23 और 26 जनवरी 2026 को सुबह 3 बजे से कार्यक्रम खत्म होने तक 6 मेट्रो स्टेशनों के चुनिंदा गेट …', 'https://images.moneycontrol.com/static-hindinews/2026/01/DMRC.jpg', 'https://hindi.moneycontrol.com/india/the-gates-of-these-six-metro-stations-will-remain-closed-on-january-23-and-26-dmrc-announced-article-2348355.html', 'Moneycontrol', '2026-01-23 03:59:50', 1, '2026-01-24 09:36:48', '2026-01-24 09:36:48'),
